// Processar dados do webhook Evolution API
const input = $input.all()[0].json;
console.log('📥 Webhook Input:', JSON.stringify(input, null, 2));

// Extrair dados da mensagem
let phone = '';
let messageText = '';
let fromMe = false;

// Formato Evolution API
if (input.data) {
  phone = input.data.key?.remoteJid || '';
  fromMe = input.data.key?.fromMe || false;
  messageText = input.data.message?.conversation || input.data.message?.extendedTextMessage?.text || '';
} else {
  // Formato alternativo
  phone = input.key?.remoteJid || input.from || '';
  fromMe = input.key?.fromMe || input.fromMe || false;
  messageText = input.message?.conversation || input.body || input.text || '';
}

// Limpeza do telefone
phone = String(phone || '').replace('@s.whatsapp.net', '').replace('@c.us', '').replace(/\D/g, '');
messageText = String(messageText || '').toLowerCase().trim();

// Verificar se é mensagem própria
if (fromMe) {
  return { shouldProcess: false, reason: 'Mensagem própria ignorada' };
}

// Keywords para consulta de saldo
const saldoKeywords = ['saldo', 'extrato', 'consulta', 'dinheiro', 'valor', 'quanto tenho', 'meu saldo'];
const isSaldoKeyword = saldoKeywords.some(keyword => messageText.includes(keyword));

if (!isSaldoKeyword) {
  return { shouldProcess: false, reason: 'Não é consulta de saldo' };
}

// Validar telefone
if (!phone || phone.length < 10) {
  return { shouldProcess: false, reason: 'Telefone inválido' };
}

console.log('✅ Processando consulta de saldo para:', phone);

return {
  phone: phone,
  message: messageText,
  shouldProcess: true,
  isSaldoKeyword: true,
  timestamp: new Date().toISOString()
};

// Processar resposta da API de saldo
const apiResponse = $input.all()[0].json;
const userData = $input.all()[1].json;

console.log('📊 API Saldo Response:', JSON.stringify(apiResponse, null, 2));

let responseMessage = '';
let success = false;

if (apiResponse && apiResponse.success) {
  responseMessage = apiResponse.message || 'Consulta realizada com sucesso!';
  success = true;
} else if (apiResponse && apiResponse.error) {
  responseMessage = `❌ ${apiResponse.error}`;
} else {
  responseMessage = '❌ Erro interno. Tente novamente em alguns instantes.';
}

return {
  phone: userData.phone,
  message: responseMessage,
  success: success,
  timestamp: new Date().toISOString()
};

// Log de mensagem ignorada
const userData = $input.all()[0].json;
console.log('⚠️ Mensagem ignorada:', userData.reason);

return {
  status: 'ignored',
  reason: userData.reason,
  timestamp: new Date().toISOString()
};
